mapper.select:
SELECT approval_no approvalNo, user_no userNo, title, content, reg_time regTime, mod_time modTime, end_time
  FROM approval.approval
 where user_no = #{userNo, jdbcType=VARCHAR}

   and approval_no = #{approvalNo, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO approval.approval
(	
	user_no, title, content, reg_time, mod_time
)
VALUES
( 
	#{userNo}
	,#{title}
	,#{content}
	,now()
	,now()
 )

INSERT INTO approval.approval_line
(	
	approval_no, user_no, seq, state, reg_time, mod_time
)
VALUES
( 
	#{approvalNo}
	,#{userNo}
	,#{seq}
	,#{state}
	,now()
	,now()
 )

mapper.update:
UPDATE approval.approval_line
   set state = #{state},
	   mod_time = now()
where approval_no = #{approvalNo}

  and user_no = #{userNo}


  and seq = #{seq}
