mapper.insert:
INSERT INTO approval.approval
(	
	user_no, title, content, reg_time, mod_time
)
VALUES
( 
	#{userNo}
	,#{title}
	,#{content}
	,now()
	,now()
 )

INSERT INTO approval.approval_line
(	
	approval_no, user_no, seq, state, reg_time, mod_time
)
VALUES
( 
	#{approvalNo}
	,#{userNo}
	,#{seq}
	,#{state}
	,now()
	,now()
 )

mapper.update:
UPDATE approval.approval_line
   set state = #{state},
	   mod_time = now()
where approval_no = #{approvalNo}
  and user_no = #{userNo}
  and state = 'REQUEST'

UPDATE approval.approval_line
   set state = #{state},
	   mod_time = now()
where approval_no = #{approvalNo}
  and seq = #{seq}
  and state = 'READY'
